import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { register } from '../services/api';
import './Register.css';

const Register = () => {
  const navigate = useNavigate();
  const [lang, setLang] = useState('en');

  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    phoneNumber: '',
    age: '',
    state: '',
    district: '',
    occupation: '',
    annualIncome: '',
    category: '',
    gender: ''
  });

  const states = [
    'Andhra Pradesh','Arunachal Pradesh','Assam','Bihar','Chhattisgarh',
    'Goa','Gujarat','Haryana','Himachal Pradesh','Jharkhand','Karnataka',
    'Kerala','Madhya Pradesh','Maharashtra','Manipur','Meghalaya','Mizoram',
    'Nagaland','Odisha','Punjab','Rajasthan','Sikkim','Tamil Nadu',
    'Telangana','Tripura','Uttar Pradesh','Uttarakhand','West Bengal'
  ];

  const occupations = [
    { en: 'Farmer', hi: 'किसान' },
    { en: 'Student', hi: 'छात्र / छात्रा' },
    { en: 'Government Employee', hi: 'सरकारी कर्मचारी' },
    { en: 'Private Employee', hi: 'निजी कर्मचारी' },
    { en: 'Self Employed', hi: 'स्व-रोजगार' },
    { en: 'Business Owner', hi: 'व्यवसायी' },
    { en: 'Unemployed', hi: 'बेरोजगार' },
    { en: 'Retired', hi: 'सेवानिवृत्त' },
    { en: 'Other', hi: 'अन्य' }
  ];

  const categories = [
    { en: 'General', hi: 'सामान्य' },
    { en: 'OBC', hi: 'अन्य पिछड़ा वर्ग' },
    { en: 'SC', hi: 'अनुसूचित जाति' },
    { en: 'ST', hi: 'अनुसूचित जनजाति' }
  ];

  const genders = [
    { en: 'Male', hi: 'पुरुष' },
    { en: 'Female', hi: 'महिला' },
    { en: 'Other', hi: 'अन्य' }
  ];

  const text = {
    en: {
      title: 'Government Scheme Portal Registration',
      subtitle: 'Register to discover government benefits',
      language: 'Language',
      firstName: 'First Name',
      lastName: 'Last Name',
      email: 'Email Address',
      password: 'Password',
      confirmPassword: 'Confirm Password',
      phoneNumber: 'Mobile Number',
      age: 'Age',
      state: 'State',
      district: 'District',
      occupation: 'Occupation',
      annualIncome: 'Annual Income (₹)',
      category: 'Category',
      gender: 'Gender',
      register: 'Register',
      haveAccount: 'Already have an account?',
      loginHere: 'Login here',
      mandatoryFields: 'All fields are mandatory',
      govIndia: 'Government of India'
    },
    hi: {
      title: 'सरकारी योजना पोर्टल पंजीकरण',
      subtitle: 'सरकारी लाभों की खोज के लिए पंजीकरण करें',
      language: 'भाषा',
      firstName: 'नाम',
      lastName: 'उपनाम',
      email: 'ईमेल पता',
      password: 'पासवर्ड',
      confirmPassword: 'पासवर्ड की पुष्टि करें',
      phoneNumber: 'मोबाइल नंबर',
      age: 'आयु',
      state: 'राज्य',
      district: 'जिला',
      occupation: 'व्यवसाय',
      annualIncome: 'वार्षिक आय (₹)',
      category: 'श्रेणी',
      gender: 'लिंग',
      register: 'पंजीकरण करें',
      haveAccount: 'पहले से खाता है?',
      loginHere: 'यहाँ लॉगिन करें',
      mandatoryFields: 'सभी फ़ील्ड अनिवार्य हैं',
      govIndia: 'भारत सरकार'
    }
  };

  const t = text[lang];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      toast.error(lang === 'en' ? 'Passwords do not match' : 'पासवर्ड मेल नहीं खाते');
      return;
    }

    try {
      const { confirmPassword, ...data } = formData;
      data.age = parseInt(data.age);
      data.annualIncome = parseFloat(data.annualIncome);

      await register(data);
      toast.success(lang === 'en' ? 'Registration successful' : 'पंजीकरण सफल');
      setTimeout(() => navigate('/login'), 1500);
    } catch (err) {
      toast.error('Registration failed');
    }
  };

  return (
    <div className="gov-register-page">
      <header className="gov-header">
        <div className="header-text">
          <h1>{t.govIndia}</h1>
          <h2>{t.title}</h2>
        </div>
      </header>

      <div className="gov-form-container">
        <div className="language-selector">
          <label>{t.language}:</label>
          <button className={lang === 'en' ? 'active' : ''} onClick={() => setLang('en')}>English</button>
          <button className={lang === 'hi' ? 'active' : ''} onClick={() => setLang('hi')}>हिन्दी</button>
        </div>

        <div className="form-card">
          <h3>{t.subtitle}</h3>
          <p className="mandatory-note">* {t.mandatoryFields}</p>

          <form onSubmit={handleSubmit}>
            {/* form stays same – untouched */}
            <button type="submit" className="btn-gov-primary">
              {t.register}
            </button>
          </form>

          <p className="login-link">
            {t.haveAccount} <a href="/login">{t.loginHere}</a>
          </p>
        </div>
      </div>

      <footer className="gov-footer">
        © 2026 {t.govIndia} | Government Scheme Portal
      </footer>
    </div>
  );
};

export default Register;
