import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { login } from '../services/api';
import './Register.css';

const Login = () => {
  const navigate = useNavigate();
  const [lang, setLang] = useState('en');
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [showOtp, setShowOtp] = useState(false);
  const [userData, setUserData] = useState(null);

  const text = {
    en: {
      title: 'Login to Government Scheme Portal',
      subtitle: 'Access your personalized dashboard',
      language: 'Language',
      email: 'Email Address',
      password: 'Password',
      login: 'Login',
      noAccount: "Don't have an account?",
      registerHere: 'Register here',
      govIndia: 'Government of India'
    },
    hi: {
      title: '‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ ‡§™‡•ã‡§∞‡•ç‡§ü‡§≤ ‡§Æ‡•á‡§Ç ‡§≤‡•â‡§ó‡§ø‡§® ‡§ï‡§∞‡•á‡§Ç',
      subtitle: '‡§Ö‡§™‡§®‡•á ‡§µ‡•ç‡§Ø‡§ï‡•ç‡§§‡§ø‡§ó‡§§ ‡§°‡•à‡§∂‡§¨‡•ã‡§∞‡•ç‡§° ‡§§‡§ï ‡§™‡§π‡•Å‡§Ç‡§ö‡•á‡§Ç',
      language: '‡§≠‡§æ‡§∑‡§æ',
      email: '‡§à‡§Æ‡•á‡§≤ ‡§™‡§§‡§æ',
      password: '‡§™‡§æ‡§∏‡§µ‡§∞‡•ç‡§°',
      login: '‡§≤‡•â‡§ó‡§ø‡§®',
      noAccount: '‡§ñ‡§æ‡§§‡§æ ‡§®‡§π‡•Ä‡§Ç ‡§π‡•à?',
      registerHere: '‡§Ø‡§π‡§æ‡§Å ‡§™‡§Ç‡§ú‡•Ä‡§ï‡§∞‡§£ ‡§ï‡§∞‡•á‡§Ç',
      govIndia: '‡§≠‡§æ‡§∞‡§§ ‡§∏‡§∞‡§ï‡§æ‡§∞'
    }
  };

  const t = text[lang];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
