import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { getAllSchemes } from '../services/api';
import './AllSchemes.css';

const AllSchemes = () => {
  const [allSchemes, setAllSchemes] = useState([]);
  const [filteredSchemes, setFilteredSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [viewMode, setViewMode] = useState('grid'); // grid or list

  // Filter states
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedState, setSelectedState] = useState('All');
  const [selectedGender, setSelectedGender] = useState('All');
  const [selectedOccupation, setSelectedOccupation] = useState('All');
  const [ageRange, setAgeRange] = useState({ min: '', max: '' });
  const [incomeLimit, setIncomeLimit] = useState('');
  const [showFilters, setShowFilters] = useState(false);

  const categories = ['All', 'Education', 'Health', 'Agriculture', 'Housing', 'Business', 'Social Welfare', 'Employment'];
  const states = ['All', 'Andhra Pradesh', 'Karnataka', 'Maharashtra', 'Tamil Nadu', 'Uttar Pradesh', 'West Bengal', 'Gujarat', 'Rajasthan', 'Kerala'];
  const genders = ['All', 'Male', 'Female', 'Other'];
  const occupations = ['All', 'Farmer', 'Student', 'Government Employee', 'Private Employee', 'Self Employed', 'Business Owner', 'Unemployed'];

  useEffect(() => {
    fetchAllSchemes();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [allSchemes, searchTerm, selectedCategory, selectedState, selectedGender, selectedOccupation, ageRange, incomeLimit]);

  const fetchAllSchemes = async () => {
    try {
      const response = await getAllSchemes();
      setAllSchemes(response.data);
      setFilteredSchemes(response.data);
      setLoading(false);
    } catch (error) {
      toast.error('Failed to fetch schemes');
      setLoading(false);
    }
  };

  const applyFilters = () => {
    let filtered = [...allSchemes];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(scheme =>
        scheme.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        scheme.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        scheme.shortDescription?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Category filter
    if (selectedCategory !== 'All') {
      filtered = filtered.filter(scheme => scheme.category === selectedCategory);
    }

    // State filter
    if (selectedState !== 'All') {
      filtered = filtered.filter(scheme =>
        scheme.eligibleStates.includes('All') ||
        scheme.eligibleStates.includes(selectedState)
      );
    }

    // Gender filter
    if (selectedGender !== 'All') {
      filtered = filtered.filter(scheme =>
        scheme.eligibleGenders.includes('All') ||
        scheme.eligibleGenders.includes(selectedGender)
      );
    }

    // Occupation filter
    if (selectedOccupation !== 'All') {
      filtered = filtered.filter(scheme =>
        !scheme.eligibleOccupations || 
        scheme.eligibleOccupations.length === 0 ||
        scheme.eligibleOccupations.includes(selectedOccupation)
      );
    }

    // Age filter
    if (ageRange.min) {
      filtered = filtered.filter(scheme =>
        !scheme.minAge || parseInt(ageRange.min) >= scheme.minAge
      );
    }
    if (ageRange.max) {
      filtered = filtered.filter(scheme =>
        !scheme.maxAge || parseInt(ageRange.max) <= scheme.maxAge
      );
    }

    // Income filter
    if (incomeLimit) {
      filtered = filtered.filter(scheme =>
        !scheme.maxIncome || parseFloat(incomeLimit) <= scheme.maxIncome
      );
    }

    setFilteredSchemes(filtered);
  };

  const resetFilters = () => {
    setSearchTerm('');
    setSelectedCategory('All');
    setSelectedState('All');
    setSelectedGender('All');
    setSelectedOccupation('All');
    setAgeRange({ min: '', max: '' });
    setIncomeLimit('');
  };

  const activeFiltersCount = () => {
    let count = 0;
    if (selectedCategory !== 'All') count++;
    if (selectedState !== 'All') count++;
    if (selectedGender !== 'All') count++;
    if (selectedOccupation !== 'All') count++;
    if (ageRange.min || ageRange.max) count++;
    if (incomeLimit) count++;
    return count;
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading schemes...</p>
      </div>
    );
  }

  return (
    <div className="all-schemes-page">
      {/* Hero Section */}
      <div className="schemes-hero">
        <div className="hero-content">
          <h1>üáÆüá≥ Explore Government Schemes</h1>
          <p className="hero-subtitle">Discover {allSchemes.length} schemes designed for your welfare</p>
          <div className="hero-stats">
            <div className="stat-card">
              <div className="stat-number">{allSchemes.length}</div>
              <div className="stat-label">Total Schemes</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{categories.length - 1}</div>
              <div className="stat-label">Categories</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{filteredSchemes.length}</div>
              <div className="stat-label">Matching Filters</div>
            </div>
          </div>
        </div>
      </div>

      <div className="schemes-container">
        {/* Search Bar */}
        <div className="search-section">
          <div className="search-bar">
            <span className="search-icon">üîç</span>
            <input
              type="text"
              placeholder="Search schemes by name, description, or keywords..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button className="clear-search" onClick={() => setSearchTerm('')}>‚úï</button>
            )}
          </div>
          <button 
            className="filter-toggle-btn"
            onClick={() => setShowFilters(!showFilters)}
          >
            üéõÔ∏è Filters {activeFiltersCount() > 0 && `(${activeFiltersCount()})`}
          </button>
          <div className="view-toggle">
            <button 
              className={`view-btn ${viewMode === 'grid' ? 'active' : ''}`}
              onClick={() => setViewMode('grid')}
              title="Grid View"
            >
              ‚ñ¶
            </button>
            <button 
              className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
              onClick={() => setViewMode('list')}
              title="List View"
            >
              ‚ò∞
            </button>
          </div>
        </div>

        {/* Advanced Filters */}
        {showFilters && (
          <div className="filters-panel">
            <div className="filters-header">
              <h3>üéØ Advanced Filters</h3>
              <button className="reset-btn" onClick={resetFilters}>
                Reset All
              </button>
            </div>

            <div className="filters-grid">
              {/* Category Filter */}
              <div className="filter-group">
                <label>üìÇ Category</label>
                <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
                  {categories.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              </div>

              {/* State Filter */}
              <div className="filter-group">
                <label>üìç State</label>
                <select value={selectedState} onChange={(e) => setSelectedState(e.target.value)}>
                  {states.map(state => (
                    <option key={state} value={state}>{state}</option>
                  ))}
                </select>
              </div>

              {/* Gender Filter */}
              <div className="filter-group">
                <label>üë§ Gender</label>
                <select value={selectedGender} onChange={(e) => setSelectedGender(e.target.value)}>
                  {genders.map(gender => (
                    <option key={gender} value={gender}>{gender}</option>
                  ))}
                </select>
              </div>

              {/* Occupation Filter */}
              <div className="filter-group">
                <label>üíº Occupation</label>
                <select value={selectedOccupation} onChange={(e) => setSelectedOccupation(e.target.value)}>
                  {occupations.map(occ => (
                    <option key={occ} value={occ}>{occ}</option>
                  ))}
                </select>
              </div>

              {/* Age Range Filter */}
              <div className="filter-group">
                <label>üéÇ Age Range</label>
                <div className="range-inputs">
                  <input
                    type="number"
                    placeholder="Min"
                    value={ageRange.min}
                    onChange={(e) => setAgeRange({ ...ageRange, min: e.target.value })}
                  />
                  <span>to</span>
                  <input
                    type="number"
                    placeholder="Max"
                    value={ageRange.max}
                    onChange={(e) => setAgeRange({ ...ageRange, max: e.target.value })}
                  />
                </div>
              </div>

              {/* Income Filter */}
              <div className="filter-group">
                <label>üí∞ Annual Income (‚Çπ)</label>
                <input
                  type="number"
                  placeholder="Max income limit"
                  value={incomeLimit}
                  onChange={(e) => setIncomeLimit(e.target.value)}
                />
              </div>
            </div>
          </div>
        )}

        {/* Active Filters Tags */}
        {activeFiltersCount() > 0 && (
          <div className="active-filters">
            <span className="filter-label">Active Filters:</span>
            {selectedCategory !== 'All' && (
              <span className="filter-tag">
                {selectedCategory}
                <button onClick={() => setSelectedCategory('All')}>‚úï</button>
              </span>
            )}
            {selectedState !== 'All' && (
              <span className="filter-tag">
                {selectedState}
                <button onClick={() => setSelectedState('All')}>‚úï</button>
              </span>
            )}
            {selectedGender !== 'All' && (
              <span className="filter-tag">
                {selectedGender}
                <button onClick={() => setSelectedGender('All')}>‚úï</button>
              </span>
            )}
            {selectedOccupation !== 'All' && (
              <span className="filter-tag">
                {selectedOccupation}
                <button onClick={() => setSelectedOccupation('All')}>‚úï</button>
              </span>
            )}
            {(ageRange.min || ageRange.max) && (
              <span className="filter-tag">
                Age: {ageRange.min || '0'} - {ageRange.max || '‚àû'}
                <button onClick={() => setAgeRange({ min: '', max: '' })}>‚úï</button>
              </span>
            )}
            {incomeLimit && (
              <span className="filter-tag">
                Income ‚â§ ‚Çπ{parseInt(incomeLimit).toLocaleString()}
                <button onClick={() => setIncomeLimit('')}>‚úï</button>
              </span>
            )}
          </div>
        )}

        {/* Results Count */}
        <div className="results-header">
          <h2>
            {filteredSchemes.length === allSchemes.length
              ? `All Schemes (${allSchemes.length})`
              : `Showing ${filteredSchemes.length} of ${allSchemes.length} schemes`}
          </h2>
        </div>

        {/* Schemes Display */}
        {filteredSchemes.length === 0 ? (
          <div className="no-results">
            <div className="no-results-icon">üîç</div>
            <h3>No schemes found</h3>
            <p>Try adjusting your filters or search term</p>
            <button className="btn-primary" onClick={resetFilters}>
              Reset All Filters
            </button>
          </div>
        ) : (
          <div className={`schemes-display ${viewMode}`}>
            {filteredSchemes.map(scheme => (
              <div key={scheme.id} className="scheme-card">
                <div className="scheme-header">
                  <div className="scheme-badges">
                    <span className={`category-badge ${scheme.category.toLowerCase().replace(/\s+/g, '-')}`}>
                      {scheme.category}
                    </span>
                    {scheme.department && (
                      <span className="dept-badge">{scheme.department}</span>
                    )}
                  </div>
                  <h3 className="scheme-title">{scheme.name}</h3>
                </div>

                <div className="scheme-body">
                  <p className="scheme-description">
                    {scheme.shortDescription || scheme.description.substring(0, 120)}...
                  </p>

                  {scheme.benefits && scheme.benefits.length > 0 && (
                    <div className="benefits-preview">
                      <strong>‚ú® Key Benefits:</strong>
                      <ul>
                        {scheme.benefits.slice(0, 3).map((benefit, idx) => (
                          <li key={idx}>{benefit}</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  <div className="scheme-meta">
                    {scheme.minAge && (
                      <span className="meta-tag">üéÇ Age: {scheme.minAge}{scheme.maxAge ? `-${scheme.maxAge}` : '+'}</span>
                    )}
                    {scheme.maxIncome && (
                      <span className="meta-tag">üí∞ Income ‚â§ ‚Çπ{scheme.maxIncome.toLocaleString()}</span>
                    )}
                    {scheme.eligibleStates && scheme.eligibleStates.length > 0 && !scheme.eligibleStates.includes('All') && (
                      <span className="meta-tag">üìç {scheme.eligibleStates.length} states</span>
                    )}
                  </div>
                </div>

                <div className="scheme-footer">
                  <Link to={`/schemes/${scheme.id}`} className="btn-view-details">
                    View Full Details ‚Üí
                  </Link>
                  {scheme.officialWebsite && (
                    <a
                      href={scheme.officialWebsite}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="btn-apply"
                    >
                      Apply Now ‚Üó
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AllSchemes;
